import React from "react";
import { useEffect } from "react";
import axios from "axios";

function Sidebar() {
	const [cat, setCat] = useState([]);
	useEffect(() => {
		const getCats = async () => {
			const res = await axios.get("/categories");
			setCat(res.data);
		};
	});
	return (
		<div>
			<div className='px-3 '>
				<h4 className='w-full text-center py-4 '>About me</h4>
				<div className='h-small2'>
					<img
						src='https://images.unsplash.com/photo-1631906440184-fe0bcba670ca?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=988&q=80'
						alt=''
						className='w-full h-full'
					/>
				</div>
				<div className='flex  flex-col items-center space-y-3 w-full'>
					<p>renders an with a real href, so people using the keyboard</p>
					<h6 className='border-t-2 border-b-2 border-gray-400 px-10'>
						Category
					</h6>
					<div className='flex space-x-4'>
						<div>
							<h4>life </h4>
							<h4>style</h4>
							<h4>Tech</h4>
						</div>
						<div>
							<h4>Music </h4>
							<h4>Sport</h4>
							<h4>Cinema</h4>
						</div>
					</div>
					<h6 className='border-t-2 border-b-2 border-gray-400 px-10'>
						Follow Us
					</h6>
				</div>
			</div>
		</div>
	);
}

export default Sidebar;
