import React from "react";
import { Link } from "react-router-dom";

function Post({ post }) {
	return (
		<div>
			<div className='   w-96  '>
				<div className='h-80 w-96 rounded-lg overflow-hidden'>
					{post.photo && (
						<img
							src='https://images.unsplash.com/photo-1632196068933-23998d66a2dd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=2370&q=80'
							alt=''
							className='h-full w-full'
						/>
					)}
				</div>
				<div className='py-4 space-y-2 px-3'>
					<div>
						<h4 className='text-center w-full text-gray-400'>music life</h4>
						<Link>
							<h1 className='text-center w-full font-extrabold text-2xl'>
								{post.title}
							</h1>
						</Link>
						<h4 className='text-center w-full text-gray-400'>
							{new Date(post.createdAt).toDateString()}
						</h4>
					</div>

					<p className='w-full text-gray-500'>{post.desc}</p>
				</div>
			</div>
		</div>
	);
}

export default Post;
