import React from "react";

import { useState, useContext } from "react";
import { Context } from "../context/Context";
import API from "./API";

function Write() {
	const [title, setTitle] = useState("");
	const [file, setFile] = useState(null);
	const [desc, setDesc] = useState("");
	const { user } = useContext(Context);

	const handleSubmit = (e) => {
		e.preventDefault();
		const newPost = {
			username: user.username,
			title,
			desc,
		};
		API.post("/posts");
	};

	return (
		<div className='flex justify-center items-center mt-16'>
			<div className='grid grid-cols-5  '>
				<div className='text-black col-span-3 col-start-2  flex flex-col'>
					<div className='w-full  rounded-2xl bg-greener-600'>
						<img
							src='https://images.unsplash.com/photo-1633455964457-46dcea6568f9?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=2370&q=80'
							alt=''
							className='object-contain'
						/>
					</div>
					<div className=''>
						<form className='w-full' onSubmit={handleSubmit}>
							<div className='w-full flex justify-between'>
								<div>
									<label for='filetype'>
										<i className='fas fa-plus rounded-full p-1 border border-black'></i>
									</label>
									<input
										type='file'
										id='filetype'
										className='hidden h-9 w-14 border border-black'
									/>
									<input type='text' placeholder='Title' className='' />
								</div>
								<button className='bg-greener-300 px-7 py-2' type='submit'>
									publish
								</button>
							</div>
							<div className='w-full'>
								<textarea
									placeholder='write your story...'
									type='text'
									className='w-full h-32'
								></textarea>
							</div>
						</form>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Write;
