import React from "react";
import Navigation from "../Navigation";
import Sidebar from "../main/Sidebar";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCoffee, faIndustry } from "@fortawesome/free-solid-svg-icons";
import { useLocation } from "react-router";
import { useEffect, useState } from "react";
import axios from "axios";
function Singlepost() {
	const location = useLocation();
	const path = location.pathname.split("/")[2];
	const [post, setPost] = useState({});

	useEffect(() => {
		const getPost = async () => {
			const res = await axios.get("/posts/", path);
			setPost(res.data);
		};
		getPost();
	}, [path]);

	return (
		<div className='col-span-3 mt-16'>
			<div className=' flex justify-center'>
				<div className='   w-big  '>
					<div className='h-80 w-big rounded-lg overflow-hidden'>
						{post.photo && (
							<img
								src='https://images.unsplash.com/photo-1632196068933-23998d66a2dd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=2370&q=80'
								alt=''
								className='h-full w-full'
							/>
						)}
					</div>
					<div className='py-4 space-y-2 px-3'>
						<div>
							<h4 className='text-center w-full text-blue-600'>
								music life <i class='fab fa-facebook'></i>
							</h4>

							<Link>
								<h1 className='text-center w-full font-extrabold text-2xl'>
									lorem ipsumm dolar main
								</h1>
							</Link>
							<div className='w-full flex justify-center space-x-4'>
								<i class='fas fa-trash-alt'></i>
								<i class='fas fa-edit'></i>
							</div>
						</div>

						<p className='w-full text-gray-500'>
							For primary navigation and marketing sections, designed toFor
							primary navigation and marketing sections, designed to
						</p>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Singlepost;
