import React from "react";
// import User from "../../../api/models/User";
import Sidebar from "./main/Sidebar";
import { Context } from "../context/Context";
import { useContext, useState } from "react";
import API from "./API";

function Setting() {
	const { user, dispatch } = useContext(Context);
	const [file, setFile] = useState(null);
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");
	const [username, setUsername] = useState("");
	const handleSubmit = async (e) => {
		e.preventDefault();
		const updatedUser = {
			userId: user._id,
			username,
			email,
			password,
		};
		if (file) {
			const data = new FormData();
			const filename = Date.now() + File.name;
			data.append("name", filename);
			data.append("file", file);
			updatedUser.profilePic = filename;

			try {
				await API.post("/upload", data);
			} catch (err) {}
		}
		try {
			await API.put("/posts" + user._id, updatedUser);
		} catch (err) {}
	};
	return (
		<div className='flex justify-center items-center mt-16 px-7'>
			<div className='grid grid-cols-3'>
				<div className='text-black col-span-2  flex flex-col'>
					<div className='w-full flex justify-between mt-8'>
						<p className='text-red-400 text-3xl'>Update Your Account</p>
						<button className='text-red-400 self-end'>Delete Account</button>
					</div>
					<h4>Profile picture</h4>
					<div className='flex items-center space-x-3'>
						<div className='w-24 h-48Â   rounded-t-3xl rounded-b-3xl bg-greener-600 overflow-hidden'>
							<img src={user.profilePic} alt='' className='h-28 object-fill' />
						</div>
						<div className=''>
							<label htmlFor='file' className='cursor-pointer'>
								<i className='fas fa-cog text-3xl text-red-300'></i>
							</label>
							<input
								type='file'
								id='file'
								style={{ display: "none" }}
								onChange={(e) => {
									setFile(e.target.files[0]);
								}}
							/>
						</div>
					</div>
					<div className=''>
						<form className='w-full'>
							<div>
								<div className='flex flex-col'>
									<label for='username'>Username</label>
									<input
										type='text'
										id='username'
										placeholder='Username'
										className=' '
									></input>
								</div>
								<div className='flex flex-col'>
									<label for='email'>Email</label>
									<input type='email' id='email' placeholder='Email'></input>
								</div>
								<div className='flex flex-col'>
									<label for='password'>Password</label>
									<input
										type='password'
										id='password'
										placeholder='Password'
									></input>
								</div>
								<div className='w-full flex justify-center mt-2'>
									<button className='bg-greener-600 py-2 px-4'>Update</button>
								</div>
							</div>
						</form>
					</div>
				</div>
				<div className='mt-9'>
					<Sidebar />
				</div>
			</div>
		</div>
	);
}

export default Setting;
